<span class="line">	<span class="token keyword">const</span> FGameplayTagContainer<span class="token operator">*</span> ParentTags <span class="token operator">=</span> <span class="token class-name">Super</span><span class="token double-colon punctuation">::</span><span class="token function">GetCooldownTags</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
